import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';
function QueryBuilder() {
  const [selvalue1, setSelectVal1] = useState('select');
  const [selvalue2, setSelectVal2] = useState('select');
  const [inputValue, setInputValue] = useState('');

  const [addRow, setAddRow] = useState(
    { rowId: "" },
    { rowId: "" },
  )

  const select1HandelChange = useCallback((sel1val) => setSelectVal1(sel1val), []);
  const select2HandelChange = useCallback((sel2val) => setSelectVal2(sel2val), []);
  const inputHandelChange = useCallback((inptVal) => setInputValue(inptVal), []);
  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 2, md: 2, lg: 2, xl: 2 }}>
              <Tag>Hello Saurabh</Tag>
            </Grid.Cell>
          </Grid>
          <Card sectioned>
            <Stack vertical spacing="extraTight">
              <FormLayout>
                <FormLayout.Group condensed>
                  <Select
                    id={''}
                    label="Select Box 1"
                    placeholder="Select"
                    options={['A', 'B', 'C', 'D']}
                    value={selvalue1}
                    onChange={select1HandelChange}
                    error={''}
                  />
                  <Select
                    id={''}
                    label="Select Box 2"
                    placeholder="Select"
                    options={['A', 'B', 'C', 'D']}
                    value={selvalue2}
                    onChange={select2HandelChange}
                    error={''}
                  />
                  <TextField
                    label="Input Field"
                    type="text"
                    value={inputValue}
                    onChange={inputHandelChange}
                    error={''}
                    autoComplete="off"
                  />
                  <Grid >
                    <Grid.Cell columnSpan={{ xs: 2, sm: 1, md: 12, lg: 6, xl: 11 }}>
                      <div className='delBtn'>
                        <Button destructive>Delete</Button>
                      </div>
                    </Grid.Cell>
                  </Grid>
                </FormLayout.Group>
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 12, sm: 12, md: 12, lg: 12, xl: 12 }}>
                    <div className='delBtn'>
                      <Button plain>Add Rows</Button>
                    </div>
                  </Grid.Cell>
                </Grid>
              </FormLayout>
              <InlineError message={''} fieldID={''} />
            </Stack>
          </Card>
        </Card>
      </Page>
    </div>
  )
}

export default QueryBuilder
