import React from 'react'
import {
  Page,
  Card,
  Button,
  Grid,
} from '@shopify/polaris'
import { useState } from 'react';
import Rows from './Rows';
import { useEffect } from 'react';
function QueryBuilder({ main, setMain }) {
  const [error, setError] = useState(false);
  const [handleSave, sethandleSave] = useState(false);
  const handleGroupData = () => {
    // setMain([...main, { groupId: Math.floor(Math.random() * 48484874), row: [] }])
    const newGrp = {
      groupId: Math.floor(Math.random() * 48484874), row: [{
        rowId: Math.floor(Math.random() * 48484874),
        sel1: "",
        sel2: "",
        sel3: "",
      }]
    }
    // console.log([...main,newGrp]);
    setMain([...main, newGrp])
  }


  useEffect(() => {
    if (handleSave) {
      main.forEach(rows => {
        rows.row.forEach(rowData => {
          if (rowData.sel1 === "") {
            setError(true);
          } else {
            setError(false);
          }
          if (rowData.sel2 === "") {
            setError(true);
          } else {
            setError(false);
          }
          if (rowData.sel3 === "") {
            setError(true);
          } else {
            setError(false);
          }
        })
      });
    }
  })

  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          {main.map((group, groupIndex) => {
            return (
              <React.Fragment key={groupIndex}>
                <p>{group.groupId}</p>
                <Rows group={group} main={main} setMain={setMain} error={error} setError={setError} />
              </React.Fragment>
            )
          }
          )}
          <span className='addGroup'>
            <Button size='slim' outline onClick={handleGroupData} >Add Groups</Button>
          </span>
          <span className='addGroup'>
            <Button size='slim' primary onClick={sethandleSave(true)} >Save</Button>
          </span>
        </Card>
      </Page>
    </div >
  )
}

export default QueryBuilder
