import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';
function QueryBuilder() {
  const [selvalue1, setSelectVal1] = useState('select');
  const [selvalue2, setSelectVal2] = useState('select');
  const [inputValue, setInputValue] = useState('');
  const [addRow, setAddRow] = useState([
    { rowId: "" },
  ]);

  const addHandelRowAdd = () => {
    setAddRow([...addRow, { rowId: "" }]);
  }

  const addHandelRowRemove = (index) => {
    const rowlist = [...addRow];
    rowlist.splice(index, 1);
    setAddRow(rowlist);
  }
  const select1HandelChange = useCallback((sel1val) => setSelectVal1(sel1val), []);
  const select2HandelChange = useCallback((sel2val) => setSelectVal2(sel2val), []);
  const inputHandelChange = useCallback((inptVal) => setInputValue(inptVal), []);
  const handelInputChange = (e, index) =>{
    const {name,value} = e.target
    const rowlist = [...addRow];
    rowlist[index][name]= value
  }
  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 2, md: 2, lg: 2, xl: 2 }}>
              <Tag>Hello Saurabh</Tag>
            </Grid.Cell>
          </Grid>
          <Card sectioned>
            <Stack vertical spacing="extraTight">
              <FormLayout>
                {addRow.map((singleRow, index) => {
                  return (
                    <FormLayout.Group key={index} condensed>
                      <Select
                        label="Select Box 1"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={selvalue1}
                        onChange={select1HandelChange}
                        error={''}
                      />
                      <Select
                        label="Select Box 2"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={selvalue2}
                        onChange={select2HandelChange}
                        error={''}
                      />
                      <TextField
                        id='inputVal'
                        name='inputVal'
                        label="Input Field"
                        type="text"
                        value={inputValue}
                        // onChange={inputHandelChange}
                        onChange={(e) => handelInputChange(e, index)}
                        error={''}
                        autoComplete="off"
                      />
                      {addRow.length > 1 && (
                        <Grid >
                          <Grid.Cell columnSpan={{ xs: 2, sm: 1, md: 12, lg: 6, xl: 11 }}>
                            <div className='delBtn'>
                              <Button onClick={() => addHandelRowRemove(index)} destructive>Delete</Button>
                            </div>
                          </Grid.Cell>
                        </Grid>
                      )}
                    </FormLayout.Group>
                  )
                })}
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 12, sm: 12, md: 12, lg: 12, xl: 12 }}>
                    <div className='delBtn'>
                      <Button onClick={addHandelRowAdd} plain>Add Rows</Button>
                    </div>
                  </Grid.Cell>
                </Grid>
              </FormLayout>
              <InlineError message={''} fieldID={''} />
            </Stack>
          </Card>
        </Card>
      </Page>
    </div>
  )
}

export default QueryBuilder
