import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';

function FormGroup() {

  const [value, setValue] = useState('');
  const [select1, setSelect1] = useState('');
  const [select2, setSelect2] = useState('');


  const [addRow, setAddRow] = useState(
    [{
      rowId: "",
      sel1: "",
      sel2: "",
      inpt: "",
    },]);

  console.log(addRow);
  const addHandelRowAdd = (index) => {
    setAddRow([...addRow, {
      rowId: "",
      sel1: "",
      sel2: "",
      inpt: "",
    },]);
  }

  const addHandelRowRemove = (index) => {
    const rowlist = [...addRow];
    select1[index] = '';
    select2[index] = '';
    rowlist.splice(index, 1);
    setAddRow(rowlist);
  }

  const handelInputChange = (e, index) => {
    const temp = [...value];
    temp[index] = e;
    setValue(temp);
  }
  return (
    <FormLayout.Group key={index} condensed>
      <Select
        label="Select Box 1"
        placeholder="Select"
        options={['A', 'B', 'C', 'D']}
        value={select1[index]}
        onChange={(e) => {
          const temp = [...select1];
          temp[index] = e;
          setSelect1(temp);
        }}
        error={''}
      />
      <Select
        label="Select Box 2"
        placeholder="Select"
        options={['A', 'B', 'C', 'D']}
        value={select2[index]}
        onChange={(e) => {
          const temp = [...select2];
          temp[index] = e;
          setSelect2(temp);
        }}
        error={''}
      />
      <TextField
        id='inputVal'
        name='inputVal'
        label="Input Field"
        type="text"
        value={value[index]}

        onChange={(e) => handelInputChange(e, index)}
        error={''}
        autoComplete="off"
      />
      {addRow.length > 1 && (
        <Grid >
          <Grid.Cell columnSpan={{ xs: 2, sm: 1, md: 12, lg: 6, xl: 11 }}>
            <div className='delBtn'>
              <Button onClick={(index) => addHandelRowRemove(index)} destructive>Delete</Button>
            </div>
          </Grid.Cell>
        </Grid>
      )}
    </FormLayout.Group>
  )
}

export default FormGroup
