import React from 'react'
import {
  Page,
  Card,
  Button,
  Grid,
} from '@shopify/polaris'
import { useState } from 'react';
import Rows from './Rows';
import { useEffect } from 'react';
function QueryBuilder({ main, setMain }) {

  const handleGroupData = () => {
    // setMain([...main, { groupId: Math.floor(Math.random() * 48484874), row: [] }])
    const newGrp = {
      groupId: Math.floor(Math.random() * 48484874), row: {
        rowId: Math.floor(Math.random() * 48484874),
        sel1: "",
        sel2: "",
        sel3: "",
      }
    }
    // console.log([...main,newGrp]);

    setMain([...main, newGrp])
  }
  const handelGroupRemove = (g) => {
    const updatedData = main.filter(rm => rm.groupId !== g)

    // console.log(updatedData,"updated");
    setMain(updatedData);
  }

  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          {main.map((group, groupIndex) => {
            return (
              <React.Fragment key={groupIndex}>
                <p>{group.groupId}</p>
                <Rows group={group} main={main} setMain={setMain} />
              </React.Fragment>
            )
          }
          )}
          <span className='addGroup'>
            <Button size='slim' outline onClick={handleGroupData} >Add Groups</Button>
          </span>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 6, md: 6, lg: 12, xl: 12 }}>
              <div className='groupBtn'>
                {main.length > 1 && (
                  <span style={{ color: '#bf0711' }} className="rmoGroup">
                    <Button outline monochrome size='slim' onClick={() => handelGroupRemove(groupIndex)} >Remove Groups</Button>
                  </span>
                )}
              </div>
            </Grid.Cell>
          </Grid>
        </Card>
      </Page>
    </div >
  )
}

export default QueryBuilder
