import logo from './logo.svg';
import './App.css';
import { AppProvider } from '@shopify/polaris';
import QueryBuilder from './pages/QueryBuilder';
import { useState } from 'react';
function App() {
  const [main, setMain] = useState(
    [{ groupId: Math.floor(Math.random() * 48484874), row: [] }]
  );
  const [mainErr,setMainErr]=useState(false);
  main.forEach(errVal=>{
    errVal.row.forEach(rowVal=>{
      console.log(rowVal, "roww");
      if(rowVal.sel1==""){
        setMainErr(true);
      }else{
        setMainErr(false);
      }
    })
  })
  return (
    <AppProvider>
      <div className="App">
        <QueryBuilder main={main} setMain={setMain} mainErr={mainErr} />
      </div>
    </AppProvider>
  );
}

export default App;
