import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';
function QueryBuilder() {
  const [rows, setRows] = useState([{
    sel1: "",
    sel2: "",
    inpt: ""
  }])
  const [addRow, setAddRow] = useState([{ rowId: "" },]);

  console.log(addRow);
  const addHandelRowAdd = () => {
    setAddRow([...addRow, { rowId: "" }]);
  }

  const addHandelRowRemove = (index) => {
    const rowlist = [...addRow];
    rowlist.splice(index, 1);
    setAddRow(rowlist);
  }

  const handelInputChange = (e, index) => {
    const { name, value } = e;
    console.log(name, "this is my name ", value, " this is my values");
    const rowlist = [...setRows];
    rowlist[index][name] = value;
    setRows(rowlist);
  }
  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 2, md: 2, lg: 2, xl: 2 }}>
              <Tag>Hello Saurabh</Tag>
            </Grid.Cell>
          </Grid>
          <Card sectioned>
            <Stack vertical spacing="extraTight">
              <FormLayout>
                {addRow.map((singleRow, index) => {
                  return (
                    <FormLayout.Group key={index} condensed>
                      <Select
                        label="Select Box 1"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={rows.sel1}
                        onChange={(e) => handelInputChange(e, index)}
                        error={''}
                      />
                      <Select
                        label="Select Box 2"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={rows.sel2}
                        onChange={(e) => handelInputChange(e, index)}
                        error={''}
                      />
                      <TextField
                        id='inputVal'
                        name='inputVal'
                        label="Input Field"
                        type="text"
                        value={rows.inpt}
                        // onChange={inputHandelChange}
                        onChange={(e) => handelInputChange(e, index)}
                        error={''}
                        autoComplete="off"
                      />
                      {addRow.length > 1 && (
                        <Grid >
                          <Grid.Cell columnSpan={{ xs: 2, sm: 1, md: 12, lg: 6, xl: 11 }}>
                            <div className='delBtn'>
                              <Button onClick={() => addHandelRowRemove(index)} destructive>Delete</Button>
                            </div>
                          </Grid.Cell>
                        </Grid>
                      )}
                    </FormLayout.Group>
                  )
                })}
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 12, sm: 12, md: 12, lg: 12, xl: 12 }}>
                    <div className='delBtn'>
                      <Button onClick={addHandelRowAdd} plain>Add Rows</Button>
                    </div>
                  </Grid.Cell>
                </Grid>
              </FormLayout>
              <InlineError message={''} fieldID={''} />
            </Stack>
          </Card>
        </Card>
      </Page>
    </div>
  )
}

export default QueryBuilder
