import React from 'react'
import {
  Page,
  Card,
  Button,
  Grid,
} from '@shopify/polaris'
import { useState } from 'react';
import Rows from './Rows';
import { useEffect } from 'react';
function QueryBuilder({main,setMain}) {

  const handleGroupData = (val, index, type) => {
    console.log("handelGroupData", val, index, type)
  }
useEffect(() => {
setMain(...main,{id: 34324,row:[{adf: "aaa"}]})
},main)
  const [addGroup, setAddGroup] = useState(
    [
      [{
        GroupId: "",
      }],
    ]
  );

  const handelGroupRemove = (groupIndex) => {
    console.log(groupIndex, addGroup, "5454")
    const temp = [...addGroup]
    temp.splice(groupIndex, 1)
    setAddGroup([...temp]);
  }

  const addHandelRowGroup = () => {
    setAddGroup([...addGroup, {
      GroupId: "",
    },])
  }


  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          {addGroup.map((group, groupIndex) => {
            return (
              <React.Fragment key={groupIndex}>
                <Rows setaddGroup={setAddGroup} key={groupIndex} groupIndex={groupIndex} handleGroupData={handleGroupData} />
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 6, sm: 6, md: 6, lg: 12, xl: 12 }}>
                    <div className='groupBtn'>
                      {addGroup.length > 1 && (
                        <span style={{ color: '#bf0711' }} className="rmoGroup">
                          <Button outline monochrome size='slim' onClick={() => handelGroupRemove(groupIndex)} >Remove Groups</Button>
                        </span>
                      )}
                    </div>
                  </Grid.Cell>
                </Grid>
              </React.Fragment>
            )
          }
          )}
          <span className='addGroup'>
            <Button size='slim' outline onClick={addHandelRowGroup} >Add Groups</Button>
          </span>
        </Card>
      </Page>
    </div >
  )
}

export default QueryBuilder
