import React from 'react'
import {
  Page,
  Card,
  Button,
  Grid,
} from '@shopify/polaris'
import { useState } from 'react';
import Groups from './Groups';
function QueryBuilder() {

  const [addGroup, setAddGroup] = useState(
    [{
      GroupId: "",
    }]
  );

  const handelGroupRemove = (groupIndex) => {
    const groupList = [...addGroup];
    groupList.splice(groupIndex, 1);
    setAddGroup(groupList);
  }

  const addHandelRowGroup = () => {
    setAddGroup([...addGroup, {
      GroupId: "",
    },])

  }

  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          {addGroup.map((group, groupIndex) => {
            return (
              <Groups groupIndex={groupIndex} setAddGroup={setAddGroup} key={groupIndex} />
            )
          }
          )}

        </Card>
      </Page>
    </div >
  )
}

export default QueryBuilder
