import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';
function QueryBuilder() {

  const [value, setValue] = useState('');
  const [select1, setSelect1] = useState([]);
  const [select2, setSelect2] = useState('');

  console.log(select1, "select1");
  const AddData = []
  select1.forEach((e) => {
    const tmp = {
      sel1: e,
    }
    AddData.push(tmp)
  })

  console.log(AddData, "newdata");
  const [addRow, setAddRow] = useState([{
    rowId: "",
  },]);

  console.log(addRow);
  const addHandelRowAdd = () => {
    setAddRow([...addRow, {
      rowId: "",
      sel1: "",
      sel2: "",
      inpt: "",
    },]);
  }

  const addHandelRowRemove = (index) => {
    const rowlist = [...addRow];
    rowlist.splice(index, 1);
    setAddRow(rowlist);
  }

  const handelInputChange = (e, index) => {
    const temp = [...value];
    temp[index] = e;
    setValue(temp);
  }
  console.log(value, 'value');
  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 2, md: 2, lg: 2, xl: 2 }}>
              <Tag>Hello Saurabh</Tag>
            </Grid.Cell>
          </Grid>
          <Card sectioned>
            <Stack vertical spacing="extraTight">
              <FormLayout>
                {addRow.map((singleRow, index) => {
                  return (
                    <FormLayout.Group key={index} condensed>
                      <Select
                        label="Select Box 1"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={select1[index]}
                        onChange={(e) => {
                          const temp = [...select1];
                          temp[index] = e;
                          setSelect1(temp);
                        }}
                        error={''}
                      />
                      <Select
                        label="Select Box 2"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={select2[index]}
                        onChange={(e) => {
                          const temp = [...select2];
                          temp[index] = e;
                          setSelect2(temp);
                        }}
                        error={''}
                      />
                      <TextField
                        id='inputVal'
                        name='inputVal'
                        label="Input Field"
                        type="text"
                        value={value[index]}

                        onChange={(e) => handelInputChange(e, index)}
                        error={''}
                        autoComplete="off"
                      />
                      {addRow.length > 1 && (
                        <Grid >
                          <Grid.Cell columnSpan={{ xs: 2, sm: 1, md: 12, lg: 6, xl: 11 }}>
                            <div className='delBtn'>
                              <Button onClick={() => addHandelRowRemove(index)} destructive>Delete</Button>
                            </div>
                          </Grid.Cell>
                        </Grid>
                      )}
                    </FormLayout.Group>
                  )
                })}
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 12, sm: 12, md: 12, lg: 12, xl: 12 }}>
                    <div className='delBtn'>
                      <Button onClick={addHandelRowAdd} plain>Add Rows</Button>
                    </div>
                  </Grid.Cell>
                </Grid>
              </FormLayout>
              <InlineError message={''} fieldID={''} />
            </Stack>
          </Card>
        </Card>
      </Page>
    </div>
  )
}

export default QueryBuilder
