import React from 'react'
import {
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { DeleteMajor } from '@shopify/polaris-icons';
import { useState } from 'react';

function Rows({ addRow, setAddRow }) {

  const [sel1, setSel1] = useState("");
  const [sel2, setSel2] = useState("");
  const [sel3, setSel3] = useState("");

  console.log(addRow, "sljfs");
  const addHandelRowAdd = () => {
    setAddRow(
      [...addRow,
      {
        rowId: Math.floor(Math.random() * 48484874),
        sel1: "",
        sel2: "",
        sel3: "",
      }
      ]
    )
  }




  const addHandelRowRemove = (index) => {
    const rowlist = [...addRow];
    rowlist.splice(index, 1);
    setAddRow(rowlist);
  }

  const handleSelect1 = (value, index) => {
    console.log(value,"val");
    setSel1(value);
    const newRow= addRow.map(row => {
      if (row.rowId === index) return {...row,value}
      return row
    })
    console.log(newRow,"newRow");
  }
  const handleSelect2 = (value, index) => {
    setSel2(value)
    const tempArr = [...addRow];
    tempArr[index]['sel2'] = value;
    setAddRow([...tempArr]);
  }
  const handleSelect3 = (value, index) => {
    setSel3(value)
    const tempArr = [...addRow];
    tempArr[index]['sel3'] = value;
    setAddRow([...tempArr]);
  }

  const options2 = [
    { label: 'Contains', value: 'contains' },
    { label: 'Equals', value: 'equals' },
    { label: 'Not Contains', value: 'notContains' },
  ];
  return (
    <Card sectioned>
      <Grid >
        <Grid.Cell columnSpan={{ xs: 6, sm: 6, md: 3, lg: 2, xl: 2 }}>
          <Tag>Hello Saurabh</Tag>
        </Grid.Cell>
      </Grid>
      <Stack vertical spacing="extraTight">
        <FormLayout>
          {addRow.map((singleRow, index) => {
            return (
              <FormLayout.Group key={index} condensed>
                <Card sectioned>
                  <Grid>
                    <Grid.Cell columnSpan={{ xs: 2, sm: 2, md: 2, lg: 4, xl: 4 }}>
                      <Select
                        label="Select Box 1"
                        placeholder="Select"
                        options={['A', 'B', 'C', 'D']}
                        value={sel1}
                        onChange={(e) => {
                          handleSelect1(e, singleRow.rowId)
                        }}
                        error={''}
                      />
                    </Grid.Cell>
                    <Grid.Cell columnSpan={{ xs: 2, sm: 2, md: 2, lg: 4, xl: 4 }}>
                      <Select
                        label="Select Box 2"
                        placeholder="Select"
                        options={options2}
                        value={sel2}
                        onChange={(e) => {
                          handleSelect2(e, singleRow.rowId)
                        }}
                        error={''}
                      />
                    </Grid.Cell>
                    {
                      sel2 === 'equals' ? (
                        <Grid.Cell columnSpan={{ xs: 1, sm: 1, md: 1, lg: 3, xl: 3 }}>
                          <Select
                            label="Select Box 2"
                            placeholder="Select"
                            options={['A', 'B', 'C', 'D']}
                            value={sel3}
                            onChange={(e) => {
                              handleSelect3(e,singleRow.rowId)
                            }}
                            error={''}
                          />
                        </Grid.Cell>
                      ) : (
                        <Grid.Cell columnSpan={{ xs: 1, sm: 1, md: 1, lg: 3, xl: 3 }}>
                          <TextField
                            id='inputVal'
                            name='inputVal'
                            label="Input Field"
                            type="text"
                            value={sel3}
                            onChange={(e) => {
                              handleSelect3(e,singleRow.rowId)
                            }}
                            error={''}
                            autoComplete="off"
                          />
                        </Grid.Cell>
                      )
                    }
                    {addRow.length > 1 && (
                      <Grid.Cell columnSpan={{ xs: 1, sm: 1, md: 1, lg: 1, xl: 1 }}>
                        <div className='delBtn'>
                          <Button onClick={() => addHandelRowRemove(index)} icon={DeleteMajor}></Button>
                        </div>
                      </Grid.Cell>
                    )}
                  </Grid>
                  {addRow.length - 1 === index && (
                    <Grid >
                      <Grid.Cell columnSpan={{ xs: 2, sm: 2, md: 6, lg: 3, xl: 3 }}>
                        <div className='delBtn'>
                          <Button onClick={addHandelRowAdd} plain>Add Rows</Button>
                        </div>
                      </Grid.Cell>
                    </Grid>
                  )
                  }
                </Card>
              </FormLayout.Group>
            )
          })
          }
        </FormLayout>
        <InlineError message={''} fieldID={''} />
      </Stack>
    </Card>
  )

}

export default Rows
