import React from 'react'
import {
  Page,
  Card,
  Stack,
  FormLayout,
  TextField,
  InlineError,
  Select,
  Button,
  Grid,
  Tag,
} from '@shopify/polaris'
import { useState, useCallback } from 'react';
import FormGroup from './FormGroup';
function QueryBuilder() {

  const [value, setValue] = useState('');
  const [select1, setSelect1] = useState('');
  const [select2, setSelect2] = useState('');


  const [addRow, setAddRow] = useState(
    [{
      rowId: "",
      sel1: "",
      sel2: "",
      inpt: "",
    },]);

  console.log(addRow);
  const addHandelRowAdd = (index) => {
    setAddRow([...addRow, {
      rowId: "",
      sel1: "",
      sel2: "",
      inpt: "",
    },]);
  }

  // const addHandelRowRemove = (index) => {
  //   const rowlist = [...addRow];
  //   select1[index] = '';
  //   select2[index] = '';
  //   rowlist.splice(index, 1);
  //   setAddRow(rowlist);
  // }

  // const handelInputChange = (e, index) => {
  //   const temp = [...value];
  //   temp[index] = e;
  //   setValue(temp);
  // }
  console.log(value, 'value');
  return (
    <div>
      <Page title="Query Builder">
        <Card sectioned>
          <Grid >
            <Grid.Cell columnSpan={{ xs: 6, sm: 2, md: 2, lg: 2, xl: 2 }}>
              <Tag>Hello Saurabh</Tag>
            </Grid.Cell>
          </Grid>
          <Card sectioned>
            <Stack vertical spacing="extraTight">
              <FormLayout>
                {addRow.map((singleRow, index) => {
                  return (
                   <FormGroup key={index}/>
                  )
                })}
                <Grid >
                  <Grid.Cell columnSpan={{ xs: 12, sm: 12, md: 12, lg: 12, xl: 12 }}>
                    <div className='delBtn'>
                      <Button onClick={(index) => addHandelRowAdd()} plain>Add Rows</Button>
                    </div>
                  </Grid.Cell>
                </Grid>
              </FormLayout>
              <InlineError message={''} fieldID={''} />
            </Stack>
          </Card>
        </Card>
      </Page>
    </div>
  )
}

export default QueryBuilder
