import logo from './logo.svg';
import './App.css';
import { AppProvider } from '@shopify/polaris';
import QueryBuilder from './pages/QueryBuilder';
import { useEffect, useState } from 'react';
function App() {
  const [main, setMain] = useState(
    [{ groupId: Math.floor(Math.random() * 48484874), row: [] }]
  );

  useEffect(() => {
    console.log(main, "45544545");
  })

  return (
    <AppProvider>
      <div className="App">
        <Grid >
          <Grid.Cell columnSpan={{ xs: 6, sm: 6, md: 3, lg: 4, xl: 4 }}>
            {main.map((rowValue) => {
              console.log(rowValue.row);
              return (
                <Tag>{rowValue.sel1}  {rowValue.sel2}  {rowValue.sel3}</Tag>
              )
            })
            }
          </Grid.Cell>
        </Grid>
        <QueryBuilder main={main} setMain={setMain} />
      </div>
    </AppProvider>
  );
}

export default App;
